import React from 'react';
import { useNavigate } from 'react-router-dom';

const About = () => {
    const navigate = useNavigate(); // Ініціалізуємо useNavigate для переходу між сторінками

    const handleContactClick = () => {
        navigate('/contact'); // Перехід на сторінку контакту
    };

    return (
        <div className="about-container">
            <h1>Про нас</h1>
            <p>Ми команда, яка любить автомобілі та прагне ділитися цією пристрастю з вами.</p>
            <button className="about-button" aria-label="Зв'язатися з нами" onClick={handleContactClick}>
                Зв'язатися з нами
            </button>
        </div>
    );
};

export default About;
