import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar'; // Імпортуємо Navbar
import Home from './components/Home';
import About from './components/About';
import Cars from './components/Cars';
import Contact from './components/Contact';
import { UserProvider } from './contexts/UserContext'; // Імпортуємо UserProvider
import './App.scss';

const App = () => {
    return (
        <UserProvider> {/* Обгортка вашого додатка у провайдер */}
            <Router>
                <Navbar /> {/* Використання Navbar компонента */}

                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/about" element={<About />} />
                    <Route path="/cars" element={<Cars />} />
                    <Route path="/contact" element={<Contact />} />
                </Routes>
            </Router>
        </UserProvider>
    );
}

export default App;
