import React, { useContext } from 'react';
import { CarContext } from '../contexts/CarContext';

const Cars = () => {
    const { cars } = useContext(CarContext); // Отримуємо дані з контексту

    return (
        <div className="cars">
            <h1>Наші автомобілі</h1>
            <div className="car-list">
                {cars.map(car => (
                    <div key={car.id} className="car-card">
                        <img src={car.image} alt={car.model} />
                        <h2>{car.model}</h2>
                        <p>{car.description}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Cars;
