import React, { useContext } from 'react';
import { UserContext } from '../contexts/UserContext'; // Імпортуємо UserContext

const UserList = () => {
    const { users } = useContext(UserContext); // Отримуємо дані з контексту

    return (
        <div>
            <h2>Список користувачів:</h2>
            <ul>
                {users.map(user => (
                    <li key={user.id}>{user.name}</li>
                ))}
            </ul>
        </div>
    );
};

export default UserList;
