import React, { useContext } from 'react';
import { UserContext } from '../contexts/UserContext';

const Home = () => {
    const { users } = useContext(UserContext); // Отримуємо дані з контексту

    return (
        <div className="home-container">
            <h1>Вітання на нашому сайті!</h1>
            <p>Це основна інформація про сайт, де ви знайдете все про автомобілі.</p>
            <h2>Користувачі:</h2>
            <ul>
                {users.map(user => (
                    <li key={user.id}>{user.name}</li> // Виводимо список користувачів
                ))}
            </ul>
        </div>
    );
};

export default Home;
